if (!doc.customer) {
    frappe.throw('Por favor, seleccione un cliente.');
}

let d = new frappe.ui.Dialog({
    title: 'Crear Nuevo Proyecto',
    fields: [
        { label: 'Nombre del Proyecto', fieldname: 'project_name', fieldtype: 'Data', reqd: 1 },
        { label: 'Fecha de Inicio', fieldname: 'start_date', fieldtype: 'Date' }
    ],
    primary_action_label: 'Crear',
    primary_action(values) {
        console.log(values);
        d.hide();
    }
});
d.show();

frappe.confirm('¿Está seguro de que desea eliminar este registro?',
    () => { /* Lógica de eliminación */ },
    () => { /* Lógica de cancelación */ }
);
