items.example:
// In a client script, preferably for the 'ToDo' Doctype or in a global script
frappe.listview_settings['ToDo'] = {
    // ... see options below
};

get_indicator(doc) {
    if (doc.status === 'Open') {
        return ["Open", "orange", "status,=,Open"];
    } else if (doc.status === 'Closed') {
        return ["Closed", "green", "status,=,Closed"];
    }
}

formatters: {
    subject(val) {
        return val.bold();
    },
    priority(val) {
        return `Priority: ${val}`;
    }
}
