items.example:
// En un script de cliente, preferiblemente para el Doctype 'ToDo' o en un script global
frappe.listview_settings['ToDo'] = {
    // ... ver opciones abajo
};

get_indicator(doc) {
    if (doc.status === 'Open') {
        return ["Abierto", "orange", "status,=,Open"];
    } else if (doc.status === 'Closed') {
        return ["Cerrado", "green", "status,=,Closed"];
    }
}

formatters: {
    subject(val) {
        return val.bold();
    },
    priority(val) {
        return `Prioridad: ${val}`;
    }
}
