let page = frappe.ui.make_app_page({
    title: 'Mi Página',
    parent: wrapper,
    single_column: true
});

// Agregar un botón independiente
page.add_inner_button('Actualizar Posts', () => update_posts());

// Agregar un botón dentro de un grupo
page.add_inner_button('Nuevo Post', () => new_post(), 'Crear');

let field = page.add_field({
    label: 'Estado',
    fieldtype: 'Select',
    fieldname: 'status',
    options: ['Abierto', 'Cerrado'],
    change() {
        console.log(field.get_value());
    }
});
