// Inside a page script (.js)
frappe.pages['my-page'].on_page_load = function(wrapper) {
    const page = frappe.ui.make_app_page({
        parent: wrapper,
        title: 'My Custom Page',
        single_column: true
    });
}

let date_filter = page.add_field({
    fieldname: 'filter_date',
    fieldtype: 'Date',
    label: 'Filter by Date'
});
