// Dentro de un script de página (.js)
frappe.pages['mi-pagina'].on_page_load = function(wrapper) {
    const page = frappe.ui.make_app_page({
        parent: wrapper,
        title: 'Mi Página Personalizada',
        single_column: true
    });
}

let date_filter = page.add_field({
    fieldname: 'filter_date',
    fieldtype: 'Date',
    label: 'Filtrar por Fecha'
});
