// Este código escanea un solo código de barras y lo muestra en la consola.
new frappe.ui.Scanner({
    dialog: true,       // abrir el escáner de la cámara en un diálogo
    multiple: false,    // detenerse después de escanear un valor
    on_scan(data) {
        console.log(data.decodedText);
    }
});

const scanner = new frappe.ui.Scanner({
    dialog: true,
    multiple: true,
    on_scan(data) {
        handle_scanned_barcode(data.decodedText);
    }
});
