new frappe.ui.Scanner({
    on_scan(data) {
        if (data && data.decodedText) {
            frappe.msgprint(`Scanned code: ${data.decodedText}`);
        }
    }
});

const scanned_codes = [];
const scanner = new frappe.ui.Scanner({
    multiple: true,
    on_scan(data) {
        scanned_codes.push(data.decodedText);
        console.log('Current codes:', scanned_codes);
    }
});

// Assuming 'scanner' is an instance of a multiple scan
setTimeout(() => {
    scanner.stop_scan();
    frappe.msgprint('Scanning stopped.');
}, 10000); // Stop after 10 seconds

const my_div = document.getElementById('scanner-container');
new frappe.ui.Scanner({
    container: my_div,
    on_scan(data) {
        my_div.innerText = `Result: ${data.decodedText}`;
    }
});
