new frappe.ui.Scanner({
    on_scan(data) {
        if (data && data.decodedText) {
            frappe.msgprint(`Código escaneado: ${data.decodedText}`);
        }
    }
});

const scanned_codes = [];
const scanner = new frappe.ui.Scanner({
    multiple: true,
    on_scan(data) {
        scanned_codes.push(data.decodedText);
        console.log('Códigos actuales:', scanned_codes);
    }
});

// Asumiendo que 'scanner' es una instancia de un escaneo múltiple
setTimeout(() => {
    scanner.stop_scan();
    frappe.msgprint('Escaneo detenido.');
}, 10000); // Detener después de 10 segundos

const my_div = document.getElementById('scanner-container');
new frappe.ui.Scanner({
    container: my_div,
    on_scan(data) {
        my_div.innerText = `Resultado: ${data.decodedText}`;
    }
});
